const $ = document.querySelector.bind(document);
const $$ = document.querySelectorAll.bind(document);

const PLAYER_STORAGE_KEY = 'MUSIC_PLAYER'

const cdElement = $('.cd');
const heading = $('header h2');
const cdThumb = $('.cd-thumb');
const audio = $('#audio');
const playBtn = $('.btn-toggle-play');
const player = $('.player');
const progress = $('#progress');
const nextBtn = $('.btn-next');
const prevBtn = $('.btn-prev');
const randomBtn = $('.btn-random');
const repeatBtn = $('.btn-repeat');
const playList = $('.playlist');

const app = {
    currentIndex: 0,
    isPlaying: false,
    isRandom: false,
    isRepeat: false,
    config: JSON.parse(localStorage.getItem(PLAYER_STORAGE_KEY)) || {},

    songs: [
        {
            name: "Tình thơ",
            singer: "Orange ft Anh Tú",
            path: "./assets/media/tinh_tho.mp3",
            image: "data:image/jpeg;base64,"
        },
        {
            name: "Có hẹn với thanh xuân",
            singer: "Xuân hạ thu đông rồi lại xuân band",
            path: "./assets/media/co_hen_voi_thanh_xuan.mp3",
            image: "data:image/jpeg;base64,"
        },
        {
            name: "Một mình có buồn không",
            singer: "Lyly",
            path: "./assets/media/mot_minh_co_buon_khong.mp3",
            image: "data:image/jpeg;base64,"
        },
        {
            name: "Một thời nhanh như một ngày",
            singer: "Doãn Hiếu",
            path: "./assets/media/mot_thoi_nhanh_nhu_mot_ngay.mp3",
            image: "data:image/jpeg;base64,"
        },
        {
            name: "Nàng thơ xứ Huế",
            singer: "Trúc Nhân",
            path: "./assets/media/nang_tho_xu_hue.mp3",
            image: "data:image/jpeg;base64,"
        },
        {
            name: "Trà sữa",
            singer: "Anh Tú ft Lyly",
            path: "./assets/media/tra_sua.mp3",
            image: "data:image/jpeg;base64,"
        },
        {
            name: "Mơ",
            singer: "Orange ft Đức Phúc",
            path: "./assets/media/mo.mp3",
            image: "data:image/jpeg;base64,"
        }
    ],

    setConfig: function(key, value) {
        this.config[key] = value;
        localStorage.setItem(PLAYER_STORAGE_KEY, JSON.stringify(this.config));
    },

    // Render màn hình danh sách bài hát
    render: function() {
        const htmls = this.songs.map((song, index) => {
            return `
                <div class="song ${index == this.currentIndex ? 'active' : ""}" data-index="${index}">
                    <div class="thumb" style="background-image: url(${song.image})"></div>
                    <div class="body">
                        <h3 class="title">${song.name}</h3>
                        <p class="author">${song.singer}</p>
                    </div>
                    <div class="option">
                        <i class="fa-solid fa-ellipsis"></i>
                    </div>
                </div>
            `
        })
        playList.innerHTML = htmls.join('');
    },

    // Định nghĩa các thuộc tính cho Object
    defineProperties: function() {
        Object.defineProperty(this, 'currentSong', {
            get: function() {
                return this.songs[this.currentIndex];
            }
        })
    },

    // Lắng nghe và Xử lý sự kiện
    handleEvents: function() {

        // Xử lý CD quay
        const cdThumbAnimate = cdThumb.animate([
            {
                transform: 'rotate(360deg)',
            }
        ], {
            duration: 10000,
            iterations: Infinity
        });
        cdThumbAnimate.pause();

        // Xử lý phóng to thu nhỏ CD
        const cdWidth = cdElement.clientWidth;
        document.onscroll = function() {
            const scrollTop = document.documentElement.scrollTop;
            const newCdWidth = cdWidth - scrollTop;

            cdElement.style.width = newCdWidth > 0 ? newCdWidth + 'px' : 0;
            cdElement.style.opacity = newCdWidth / cdWidth;
        }

        // Xử lý thay đổi nút play
        playBtn.onclick = function() {
            if(app.isPlaying) {
                audio.pause();
                cdThumbAnimate.pause();
            } else {
                audio.play();
                cdThumbAnimate.play();
            }
        }

        // Lắng nghe trạng thái playing
        audio.onplay = function() {
            app.isPlaying = true;
            player.classList.add('playing');
        }

        // Lắng nghe trạng thái pause
        audio.onpause = function() {
            app.isPlaying = false;
            player.classList.remove('playing');
        }

        // Lắng nghe sự kiện khi tiến độ bài hát thay đổi
        audio.ontimeupdate = function(e) {
            if(e.target.duration) {
                const progressPercent = (e.target.currentTime / e.target.duration * 100);
                progress.value = progressPercent;
            }
        }

        // Xử lý khi tua
        progress.onchange = function(e) {
            const seekTime = e.target.value * audio.duration / 100;
            audio.currentTime = seekTime;
        }

        // Lắng nghe nút Next
        nextBtn.onclick = function nextSong() {
            if(app.isRandom) {
                app.playRandomSong();
            } else {
                app.nextSong();
            }
            audio.play();
            cdThumbAnimate.play();

            // active song
            app.render();

            // scroll active song
            app.scrollToActiveSong();
        }

        // Lắng nghe nút Prev
        prevBtn.onclick = function() {
            if(app.isRandom) {
                app.playRandomSong();
            } else {
                app.prevSong();
            }
            audio.play();
            cdThumbAnimate.play();

            // active song
            app.render();

            // scroll active song
            app.scrollToActiveSong();
        }

        // Lắng nghe nút Random
        randomBtn.onclick = function() {
            app.isRandom = !app.isRandom;
            app.setConfig('isRandom', app.isRandom);
            randomBtn.classList.toggle('active', app.isRandom);
        }

        // Xử lý phát lại
        repeatBtn.onclick = function() {
            app.isRepeat = !app.isRepeat;
            app.setConfig('isRepeat', app.isRepeat);
            repeatBtn.classList.toggle('active', app.isRepeat);
        }

        // Xử lý next song khi audio ended
        audio.onended = function() {
            if(app.isRepeat) {
                audio.play();
            } else {
                nextBtn.click();
            }
        }

        // Lắng nghe playlist
        playList.onclick = function(e) {
            
            // Trả về element .song khi click vào bất cứ element nào nằm trong .song trừ active
            const songElement = e.target.closest('.song:not(.active)');

            if(songElement || e.target.closest('.option')) {
                
                // Xử lý khi click vào option
                if(e.target.closest('.option')) {
                    console.log(e.target.closest('.option'));
                }
                
                // Xử lý khi click vào song element
                else if(songElement) {
                    app.currentIndex = songElement.dataset.index;
                    app.loadCurrentSong();
                    app.render();
                    audio.play();
                    cdThumbAnimate.play();
                }
            }
        }
    },

    // Load thông tin bài hát hiện tại vào UI
    loadCurrentSong: function() {
        heading.innerHTML = this.currentSong.name;
        cdThumb.style.backgroundImage = `url('${this.currentSong.image}')`;
        audio.src = this.currentSong.path;
    },

    // Load cấu hình web (repeat, random)
    loadConfig: function() {
        this.isRandom = this.config.isRandom;
        this.isRepeat = this.config.isRepeat;
    },

    // Next
    nextSong: function() {
        this.currentIndex++;
        if(this.currentIndex >= this.songs.length) {
            this.currentIndex = 0;
        }
        this.loadCurrentSong();
    },

    // Prev
    prevSong: function() {
        this.currentIndex--;
        if(this.currentIndex < 0) {
            this.currentIndex = this.songs.length - 1;
        }
        this.loadCurrentSong();
    },

    // Play Random song
    playRandomSong: function() {
        let newIndex;
        do {
            newIndex = Math.floor(Math.random() * this.songs.length);
        }
        while(newIndex == this.currentIndex);
        this.currentIndex = newIndex;
        this.loadCurrentSong();
    },

    // Kéo màn hình tới Active song
    scrollToActiveSong: function() {
        $('.song.active').scrollIntoView({
            behavior: 'smooth',
            block: 'end'
        })
    },

    start: function() {
        this.loadConfig();
        this.defineProperties();
        this.handleEvents();
        this.loadCurrentSong();
        this.render();

        // Load trạng thái trang web ra màn hình
        randomBtn.classList.toggle('active', app.isRandom);
        repeatBtn.classList.toggle('active', app.isRepeat);
    }
}

app.start();